
public class PrePostDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 3;
		i++;
		System.out.println(i);
		
		++i;
		System.out.println(i);
		
		System.out.println(++i);
		
		System.out.println(i++);
		
		System.out.println(i);
		
		System.out.println();
		
		int x = 5;
		int y;
		x++;
		
		y = ++x;
		System.out.println(y);
		System.out.println(x);
		y = x++;
		System.out.println(y);
		System.out.println(x);
	}

}
